import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Plane } from 'src/app/Models/Plane/plane';
import { PlaneService } from 'src/app/Services/Plane/plane.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  planes: Plane

  constructor(private planeservice: PlaneService, private router:Router) {
    this.planes = new Plane()
   }

  ngOnInit(): void {
    this.planeservice.GetPlainBySourceToDestination(this.planes.source , this.planes.destination).subscribe(res=>{
      console.log(res)
      Swal.fire(
        'Plane',
        'Edit Successfully',
        'success'
      )
      this.router.navigate(['displayplane'])
    })
  }

}
